contentCss:
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@600;700&family=Spectral:ital,wght@0,400;0,500;0,600;0,700;1,300;1,400&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Archivo:wght@700&display=swap');

body {
  font-family:'Georgia', serif ;
  margin: 1rem;
}

::selection{
    background: #6704E6;
    color: #fff;
}

table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

lessVariables:
@background-color: rgba(28, 28, 34, 1);
@toolbar-background-color: @background-color;
@menubar-background-color: @background-color;
@border-color: rgba(21, 21, 25, 0);
@color-black: #222f3e;
@color-white: #fff;
@color-tint: rgba(103, 4, 230, 1);
@color-error: rgba(255, 87, 118, 1);
@color-success: rgba(149, 222, 94, 1);
@base-value: 16px;
@control-border-radius: 8px;
@panel-border-radius: 8px;
@tinymce-border-radius: 8px;
@font-stack: "Spectral", sans-serif;
@font-weight-normal: normal;
@font-weight-bold: bold;
@toolbar-button-icon-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-text-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-hover-background-color: rgba(103, 4, 230, 1);
@toolbar-button-hover-icon-color: rgba(255, 255, 255, 1);
@toolbar-button-hover-text-color: rgba(255, 255, 255, 1);
@toolbar-button-focus-background-color: @toolbar-button-hover-background-color;
@toolbar-button-focus-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-focus-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-background-color: @toolbar-button-hover-background-color;
@toolbar-button-enabled-background-color: @toolbar-button-hover-background-color;
@toolbar-button-active-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-text-color: @toolbar-button-hover-icon-color;
